function [Anp,Anm,lambda]=computeSpectralDecompositionEulerEquations(...
         gamma,r,wxyz,g,n)
         % Compute spectral decomposition for Euler equations

% Compute parameters as in Rohde2001
ngp=size(wxyz,1);
nsd=size(wxyz,2);
wx=wxyz(:,1);
wy=wxyz(:,2);
u=wx./r;
v=wy./r;
nx=n(:,1);
ny=n(:,2);
if nsd==2
  vn=u.*nx+v.*ny;
  ek=(u.^2+v.^2)/2;
elseif nsd==3
  wz=wxyz(:,3);
  w=wz./r;
  nz=n(:,3);
  vn=u.*nx+v.*ny+w.*nz;
  ek=(u.^2+v.^2+w.^2)/2;
end
p=(gamma-1)*(g-r.*ek);
a=sqrt(gamma*p./r);
h=a.^2/(gamma-1);
ho=h+ek;

% Eigenvalues
if nsd==2
  lambda=[vn-a, vn, vn+a, vn];
elseif nsd==3
  lambda=[vn-a, vn, vn+a, vn, vn];
end

% Compute "positive" and "negative" part of the Jacobian matrix
Anp=zeros(ngp,nsd+2,nsd+2);
Anm=zeros(ngp,nsd+2,nsd+2);
if nsd==2
  
  % Compute "positive part" of the Jacobian matrix
  Anp(:,1,1)=abs(vn)/2-(vn.*(abs(a+vn)-abs(a-vn)))./(4*a)+(ek.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
  Anp(:,1,2)=nx/2+(nx.*(abs(a+vn)-abs(a-vn)))./(4*a)-(u.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
  Anp(:,1,3)=ny/2+(ny.*(abs(a+vn)-abs(a-vn)))./(4*a)-(v.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
  Anp(:,1,4)=((gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
  Anp(:,2,1)=(ek.*nx.*(gamma-1))/2-(ny.*abs(vn).*(ny.*u-nx.*v))/2-(u.*vn)/2+(abs(a-vn).*(u-a.*nx).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)-(u.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)-(abs(a+vn).*(u+a.*nx).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
  Anp(:,2,2)=vn/2+(ny.^2.*abs(vn))/2-(nx.*u.*(gamma-2))/2-(abs(a-vn).*(u-a.*nx).*(a.*nx+u.*(gamma-1)))./(4*a.^2)+(u.^2.*abs(vn).*(gamma-1))./(2*a.^2)+(abs(a+vn).*(u+a.*nx).*(a.*nx-u.*(gamma-1)))./(4*a.^2);
  Anp(:,2,3)=(ny.*u)/2-(nx.*v.*(gamma-1))/2-(nx.*ny.*abs(vn))/2-(abs(a-vn).*(u-a.*nx).*(a.*ny+v.*(gamma-1)))./(4*a.^2)+(abs(a+vn).*(u+a.*nx).*(a.*ny-v.*(gamma-1)))./(4*a.^2)+(u.*v.*abs(vn).*(gamma-1))./(2*a.^2);
  Anp(:,2,4)=(nx.*(gamma-1))/2+(u.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2)+(nx.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
  Anp(:,3,1)=(nx.*abs(vn).*(ny.*u-nx.*v))/2-(v.*vn)/2+(ek.*ny.*(gamma-1))/2+(abs(a-vn).*(v-a.*ny).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)-(v.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)-(abs(a+vn).*(v+a.*ny).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
  Anp(:,3,2)=(nx.*v)/2-(ny.*u.*(gamma-1))/2-(nx.*ny.*abs(vn))/2-(abs(a-vn).*(v-a.*ny).*(a.*nx+u.*(gamma-1)))./(4*a.^2)+(abs(a+vn).*(v+a.*ny).*(a.*nx-u.*(gamma-1)))./(4*a.^2)+(u.*v.*abs(vn).*(gamma-1))./(2*a.^2);
  Anp(:,3,3)=vn/2+(nx.^2.*abs(vn))/2-(ny.*v.*(gamma-2))/2-(abs(a-vn).*(v-a.*ny).*(a.*ny+v.*(gamma-1)))./(4*a.^2)+(v.^2.*abs(vn).*(gamma-1))./(2*a.^2)+(abs(a+vn).*(v+a.*ny).*(a.*ny-v.*(gamma-1)))./(4*a.^2);
  Anp(:,3,4)=(ny.*(gamma-1))/2+(v.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2)+(ny.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
  Anp(:,4,1)=(abs(a-vn).*(a.*vn+ek.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)-(abs(vn).*(ny.*u-nx.*v).^2)/2-(vn.*(ho-ek.*(gamma-1)))/2-(ek.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)-(abs(a+vn).*(a.*vn-ek.*(gamma-1)).*(ho+a.*vn))./(4*a.^2);
  Anp(:,4,2)=(ho.*nx)/2-(u.*vn.*(gamma-1))/2+(ny.*abs(vn).*(ny.*u-nx.*v))/2-(abs(a-vn).*(a.*nx+u.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)+(abs(a+vn).*(a.*nx-u.*(gamma-1)).*(ho+a.*vn))./(4*a.^2)+(ek.*u.*abs(vn).*(gamma-1))./(2*a.^2);
  Anp(:,4,3)=(ho.*ny)/2-(v.*vn.*(gamma-1))/2-(nx.*abs(vn).*(ny.*u-nx.*v))/2-(abs(a-vn).*(a.*ny+v.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)+(abs(a+vn).*(a.*ny-v.*(gamma-1)).*(ho+a.*vn))./(4*a.^2)+(ek.*v.*abs(vn).*(gamma-1))./(2*a.^2);
  Anp(:,4,4)=(gamma.*vn)/2+((gamma-1).*(ho.*abs(a+vn)-2*ek.*abs(vn)+ho.*abs(a-vn)))./(4*a.^2)+(vn.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
  
  % Compute "negative part" of the Jacobian matrix
  Anm(:,1,1)=(vn.*(abs(a+vn)-abs(a-vn)))./(4*a)-abs(vn)/2-(ek.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
  Anm(:,1,2)=nx/2-(nx.*(abs(a+vn)-abs(a-vn)))./(4*a)+(u.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
  Anm(:,1,3)=ny/2-(ny.*(abs(a+vn)-abs(a-vn)))./(4*a)+(v.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
  Anm(:,1,4)=-((gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
  Anm(:,2,1)=(ny.*abs(vn).*(ny.*u-nx.*v))/2-(u.*vn)/2+(ek.*nx.*(gamma-1))/2-(abs(a-vn).*(u-a.*nx).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)+(u.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)+(abs(a+vn).*(u+a.*nx).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
  Anm(:,2,2)=vn/2-(ny.^2.*abs(vn))/2-(nx.*u.*(gamma-2))/2+(abs(a-vn).*(u-a.*nx).*(a.*nx+u.*(gamma-1)))./(4*a.^2)-(u.^2.*abs(vn).*(gamma-1))./(2*a.^2)-(abs(a+vn).*(u+a.*nx).*(a.*nx-u.*(gamma-1)))./(4*a.^2);
  Anm(:,2,3)=(ny.*u)/2-(nx.*v.*(gamma-1))/2+(nx.*ny.*abs(vn))/2+(abs(a-vn).*(u-a.*nx).*(a.*ny+v.*(gamma-1)))./(4*a.^2)-(abs(a+vn).*(u+a.*nx).*(a.*ny-v.*(gamma-1)))./(4*a.^2)-(u.*v.*abs(vn).*(gamma-1))./(2*a.^2);
  Anm(:,2,4)=(nx.*(gamma-1))/2-(u.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2)-(nx.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
  Anm(:,3,1)=(ek.*ny.*(gamma-1))/2-(nx.*abs(vn).*(ny.*u-nx.*v))/2-(v.*vn)/2-(abs(a-vn).*(v-a.*ny).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)+(v.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)+(abs(a+vn).*(v+a.*ny).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
  Anm(:,3,2)=(nx.*v)/2-(ny.*u.*(gamma-1))/2+(nx.*ny.*abs(vn))/2+(abs(a-vn).*(v-a.*ny).*(a.*nx+u.*(gamma-1)))./(4*a.^2)-(abs(a+vn).*(v+a.*ny).*(a.*nx-u.*(gamma-1)))./(4*a.^2)-(u.*v.*abs(vn).*(gamma-1))./(2*a.^2);
  Anm(:,3,3)=vn/2-(nx.^2.*abs(vn))/2-(ny.*v.*(gamma-2))/2+(abs(a-vn).*(v-a.*ny).*(a.*ny+v.*(gamma-1)))./(4*a.^2)-(v.^2.*abs(vn).*(gamma-1))./(2*a.^2)-(abs(a+vn).*(v+a.*ny).*(a.*ny-v.*(gamma-1)))./(4*a.^2);
  Anm(:,3,4)=(ny.*(gamma-1))/2-(v.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2)-(ny.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
  Anm(:,4,1)=(abs(vn).*(ny.*u-nx.*v).^2)/2-(vn.*(ho-ek.*(gamma-1)))/2-(abs(a-vn).*(a.*vn+ek.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)+(ek.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)+(abs(a+vn).*(a.*vn-ek.*(gamma-1)).*(ho+a.*vn))./(4*a.^2);
  Anm(:,4,2)=(ho.*nx)/2-(u.*vn.*(gamma-1))/2-(ny.*abs(vn).*(ny.*u-nx.*v))/2+(abs(a-vn).*(a.*nx+u.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)-(abs(a+vn).*(a.*nx-u.*(gamma-1)).*(ho+a.*vn))./(4*a.^2)-(ek.*u.*abs(vn).*(gamma-1))./(2*a.^2);
  Anm(:,4,3)=(ho.*ny)/2-(v.*vn.*(gamma-1))/2+(nx.*abs(vn).*(ny.*u-nx.*v))/2+(abs(a-vn).*(a.*ny+v.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)-(abs(a+vn).*(a.*ny-v.*(gamma-1)).*(ho+a.*vn))./(4*a.^2)-(ek.*v.*abs(vn).*(gamma-1))./(2*a.^2);
  Anm(:,4,4)=(gamma.*vn)/2-((gamma-1).*(ho.*abs(a+vn)-2*ek.*abs(vn)+ho.*abs(a-vn)))./(4*a.^2)-(vn.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
  
elseif nsd==3
  
  % Get best direction to avoid singularity
  [~,d]=max(min(abs(n)));
  
  switch d
    case 1 % Terms with singularity in nx (that is avoided)
      
      % Compute "positive part" of the Jacobian matrix
      Anp(:,1,1)=abs(vn)/2-(vn.*(abs(a+vn)-abs(a-vn)))./(4*a)+(ek.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anp(:,1,2)=nx/2+(nx.*(abs(a+vn)-abs(a-vn)))./(4*a)-(u.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anp(:,1,3)=ny/2+(ny.*(abs(a+vn)-abs(a-vn)))./(4*a)-(v.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anp(:,1,4)=nz/2+(nz.*(abs(a+vn)-abs(a-vn)))./(4*a)-(w.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anp(:,1,5)=((gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anp(:,2,1)=(ek.*nx.*(gamma-1))/2-(u.*vn)/2+(abs(a-vn).*(u-a.*nx).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)-(u.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)+(ny.*abs(vn).*(v-ny.*vn))./(2*nx)+(nz.*abs(vn).*(w-nz.*vn))./(2*nx)-(abs(a+vn).*(u+a.*nx).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
      Anp(:,2,2)=vn/2+(ny.^2.*abs(vn))/2+(nz.^2.*abs(vn))/2-(nx.*u.*(gamma-2))/2-(abs(a-vn).*(u-a.*nx).*(a.*nx+u.*(gamma-1)))./(4*a.^2)+(u.^2.*abs(vn).*(gamma-1))./(2*a.^2)+(abs(a+vn).*(u+a.*nx).*(a.*nx-u.*(gamma-1)))./(4*a.^2);
      Anp(:,2,3)=(ny.*u)/2-(nx.*v.*(gamma-1))/2+(ny.*nz.^2.*abs(vn))./(2*nx)-(abs(a-vn).*(u-a.*nx).*(a.*ny+v.*(gamma-1)))./(4*a.^2)+(ny.*abs(vn).*(ny.^2-1))./(2*nx)+(abs(a+vn).*(u+a.*nx).*(a.*ny-v.*(gamma-1)))./(4*a.^2)+(u.*v.*abs(vn).*(gamma-1))./(2*a.^2);
      Anp(:,2,4)=(nz.*u)/2-(nx.*w.*(gamma-1))/2+(ny.^2.*nz.*abs(vn))./(2*nx)-(abs(a-vn).*(u-a.*nx).*(a.*nz+w.*(gamma-1)))./(4*a.^2)+(nz.*abs(vn).*(nz.^2-1))./(2*nx)+(abs(a+vn).*(u+a.*nx).*(a.*nz-w.*(gamma-1)))./(4*a.^2)+(u.*w.*abs(vn).*(gamma-1))./(2*a.^2);
      Anp(:,2,5)=(nx.*(gamma-1))/2+(u.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2)+(nx.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
      Anp(:,3,1)=(ek.*ny.*(gamma-1))/2-(abs(vn).*(v-ny.*vn))/2-(v.*vn)/2+(abs(a-vn).*(v-a.*ny).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)-(v.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)-(abs(a+vn).*(v+a.*ny).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
      Anp(:,3,2)=(nx.*v)/2-(ny.*u.*(gamma-1))/2-(nx.*ny.*abs(vn))/2-(abs(a-vn).*(v-a.*ny).*(a.*nx+u.*(gamma-1)))./(4*a.^2)+(abs(a+vn).*(v+a.*ny).*(a.*nx-u.*(gamma-1)))./(4*a.^2)+(u.*v.*abs(vn).*(gamma-1))./(2*a.^2);
      Anp(:,3,3)=vn/2-(abs(vn).*(ny.^2-1))/2-(ny.*v.*(gamma-2))/2-(abs(a-vn).*(v-a.*ny).*(a.*ny+v.*(gamma-1)))./(4*a.^2)+(v.^2.*abs(vn).*(gamma-1))./(2*a.^2)+(abs(a+vn).*(v+a.*ny).*(a.*ny-v.*(gamma-1)))./(4*a.^2);
      Anp(:,3,4)=(nz.*v)/2-(ny.*w.*(gamma-1))/2-(ny.*nz.*abs(vn))/2-(abs(a-vn).*(v-a.*ny).*(a.*nz+w.*(gamma-1)))./(4*a.^2)+(abs(a+vn).*(v+a.*ny).*(a.*nz-w.*(gamma-1)))./(4*a.^2)+(v.*w.*abs(vn).*(gamma-1))./(2*a.^2);
      Anp(:,3,5)=(ny.*(gamma-1))/2+(v.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2)+(ny.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
      Anp(:,4,1)=(ek.*nz.*(gamma-1))/2-(abs(vn).*(w-nz.*vn))/2-(vn.*w)/2+(abs(a-vn).*(w-a.*nz).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)-(w.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)-(abs(a+vn).*(w+a.*nz).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
      Anp(:,4,2)=(nx.*w)/2-(nz.*u.*(gamma-1))/2-(nx.*nz.*abs(vn))/2-(abs(a-vn).*(w-a.*nz).*(a.*nx+u.*(gamma-1)))./(4*a.^2)+(abs(a+vn).*(w+a.*nz).*(a.*nx-u.*(gamma-1)))./(4*a.^2)+(u.*w.*abs(vn).*(gamma-1))./(2*a.^2);
      Anp(:,4,3)=(ny.*w)/2-(nz.*v.*(gamma-1))/2-(ny.*nz.*abs(vn))/2-(abs(a-vn).*(w-a.*nz).*(a.*ny+v.*(gamma-1)))./(4*a.^2)+(abs(a+vn).*(w+a.*nz).*(a.*ny-v.*(gamma-1)))./(4*a.^2)+(v.*w.*abs(vn).*(gamma-1))./(2*a.^2);
      Anp(:,4,4)=vn/2-(abs(vn).*(nz.^2-1))/2-(nz.*w.*(gamma-2))/2-(abs(a-vn).*(w-a.*nz).*(a.*nz+w.*(gamma-1)))./(4*a.^2)+(w.^2.*abs(vn).*(gamma-1))./(2*a.^2)+(abs(a+vn).*(w+a.*nz).*(a.*nz-w.*(gamma-1)))./(4*a.^2);
      Anp(:,4,5)=(nz.*(gamma-1))/2+(w.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2)+(nz.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
      Anp(:,5,1)=(abs(vn).*(v-ny.*vn).*(ny.*u-nx.*v))./(2*nx)-(vn.*(ho-ek.*(gamma-1)))/2+(abs(vn).*(w-nz.*vn).*(nz.*u-nx.*w))./(2*nx)+(abs(a-vn).*(a.*vn+ek.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)-(ek.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)-(abs(a+vn).*(a.*vn-ek.*(gamma-1)).*(ho+a.*vn))./(4*a.^2);
      Anp(:,5,2)=(ho.*nx)/2-(u.*vn.*(gamma-1))/2+(ny.*abs(vn).*(ny.*u-nx.*v))/2+(nz.*abs(vn).*(nz.*u-nx.*w))/2-(abs(a-vn).*(a.*nx+u.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)+(abs(a+vn).*(a.*nx-u.*(gamma-1)).*(ho+a.*vn))./(4*a.^2)+(ek.*u.*abs(vn).*(gamma-1))./(2*a.^2);
      Anp(:,5,3)=(ho.*ny)/2-(v.*vn.*(gamma-1))/2+(abs(vn).*(ny.^2-1).*(ny.*u-nx.*v))./(2*nx)-(abs(a-vn).*(a.*ny+v.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)+(abs(a+vn).*(a.*ny-v.*(gamma-1)).*(ho+a.*vn))./(4*a.^2)+(ek.*v.*abs(vn).*(gamma-1))./(2*a.^2)+(ny.*nz.*abs(vn).*(nz.*u-nx.*w))./(2*nx);
      Anp(:,5,4)=(ho.*nz)/2-(vn.*w.*(gamma-1))/2+(abs(vn).*(nz.^2-1).*(nz.*u-nx.*w))./(2*nx)-(abs(a-vn).*(a.*nz+w.*(gamma-1)).*(ho-a.*vn))./(4*a.^2)+(abs(a+vn).*(a.*nz-w.*(gamma-1)).*(ho+a.*vn))./(4*a.^2)+(ek.*w.*abs(vn).*(gamma-1))./(2*a.^2)+(ny.*nz.*abs(vn).*(ny.*u-nx.*v))./(2*nx);
      Anp(:,5,5)=(gamma.*vn)/2+((gamma-1).*(ho.*abs(a+vn)-2*ek.*abs(vn)+ho.*abs(a-vn)))./(4*a.^2)+(vn.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
      
      % Compute "negative part" of the Jacobian matrix
      Anm(:,1,1)=(vn.*(abs(a+vn)-abs(a-vn)))./(4*a)-abs(vn)/2-(ek.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anm(:,1,2)=nx/2-(nx.*(abs(a+vn)-abs(a-vn)))./(4*a)+(u.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anm(:,1,3)=ny/2-(ny.*(abs(a+vn)-abs(a-vn)))./(4*a)+(v.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anm(:,1,4)=nz/2-(nz.*(abs(a+vn)-abs(a-vn)))./(4*a)+(w.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anm(:,1,5)=-((gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2);
      Anm(:,2,1)=(ek.*nx.*(gamma-1))/2-(u.*vn)/2-(abs(a-vn).*(u-a.*nx).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)+(u.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)-(ny.*abs(vn).*(v-ny.*vn))./(2*nx)-(nz.*abs(vn).*(w-nz.*vn))./(2*nx)+(abs(a+vn).*(u+a.*nx).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
      Anm(:,2,2)=vn/2-(ny.^2.*abs(vn))/2-(nz.^2.*abs(vn))/2-(nx.*u.*(gamma-2))/2+(abs(a-vn).*(u-a.*nx).*(a.*nx+u.*(gamma-1)))./(4*a.^2)-(u.^2.*abs(vn).*(gamma-1))./(2*a.^2)-(abs(a+vn).*(u+a.*nx).*(a.*nx-u.*(gamma-1)))./(4*a.^2);
      Anm(:,2,3)=(ny.*u)/2-(nx.*v.*(gamma-1))/2-(ny.*nz.^2.*abs(vn))./(2*nx)+(abs(a-vn).*(u-a.*nx).*(a.*ny+v.*(gamma-1)))./(4*a.^2)-(ny.*abs(vn).*(ny.^2-1))./(2*nx)-(abs(a+vn).*(u+a.*nx).*(a.*ny-v.*(gamma-1)))./(4*a.^2)-(u.*v.*abs(vn).*(gamma-1))./(2*a.^2);
      Anm(:,2,4)=(nz.*u)/2-(nx.*w.*(gamma-1))/2-(ny.^2.*nz.*abs(vn))./(2*nx)+(abs(a-vn).*(u-a.*nx).*(a.*nz+w.*(gamma-1)))./(4*a.^2)-(nz.*abs(vn).*(nz.^2-1))./(2*nx)-(abs(a+vn).*(u+a.*nx).*(a.*nz-w.*(gamma-1)))./(4*a.^2)-(u.*w.*abs(vn).*(gamma-1))./(2*a.^2);
      Anm(:,2,5)=(nx.*(gamma-1))/2-(u.*(gamma-1).*(abs(a+vn)-2*abs(vn)+abs(a-vn)))./(4*a.^2)-(nx.*(abs(a+vn)-abs(a-vn)).*(gamma-1))./(4*a);
      Anm(:,3,1)=(abs(vn).*(v-ny.*vn))/2-(v.*vn)/2+(ek.*ny.*(gamma-1))/2-(abs(a-vn).*(v-a.*ny).*(a.*vn+ek.*(gamma-1)))./(4*a.^2)+(v.*abs(vn).*(ek.*(gamma-1)-a.^2))./(2*a.^2)+(abs(a+vn).*(v+a.*ny).*(a.*vn-ek.*(gamma-1)))./(4*a.^2);
